MLControlPanelUi = Ext.extend(Ext.Panel, {
    height: 40,
    layout:'hbox',
    layoutConfig: {
        align:'middle'
    },

    //width: 100,
    //style: "border-bottom: 100px;",

    initComponent: function() {
        Ext.applyIf(this, {
            frame:true,
            items: [{
                  xtype:'button',
                  text:'Play',
                  flex:1,
                  handler: this.playBack,
                  scope: this
                },
                {
                    xtype: 'slider',
                    id:'slider',
                    height: 40,
                    value: 0,
                    increment: 1,
                    minValue: 0,
                    maxValue: 6,
                    flex: 10
                }
            ]
        });

        MLControlPanelUi.superclass.initComponent.call(this);
    },
    playBack : function(button) {
      var b = button;
      b.disable();
      var slider = Ext.getCmp('slider');
      var delay = 1000;
      for(var i=0, j=0; i <= slider.maxValue; i++) {
        setTimeout(function() {
          slider.setValue(j);
          j++;
          if(j==i)
            b.enable();
        }, delay);
        delay = delay + 500;
      }
    }
});
